import { useState } from "react";
import { Link, useNavigate } from "react-router-dom"

const Vibe = () => {

    const [vibeTitle, setVibeTitle] = useState('');
    const [isPending, setIsPending] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        const vibe = { vibeTitle };

        setIsPending(true);

        fetch('http://localhost:8000/blogs', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(vibe)
        }).then(() => {
            console.log('new vibe added');
            setIsPending(false);
            // navigate('/')
        })

    }

    return ( 
        <div className="vibe">
            <h1>¿what’s the vibe?</h1>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text"
                    required
                    value={vibeTitle}
                    onChange={(e) => setVibeTitle(e.target.value)}
                />
                { !isPending && <button>add mood</button>}
                { isPending && <button disabled>adding mood...</button>}
            </form>

        </div>
     );
}

export default Vibe;
