// to run JSON server: npx json-server --watch data/db.json --port 8000

import { useState, useEffect } from "react";
import BlogList from "./BlogList";

const Home = () => {
    const [blogs, setBlogs] = useState(null);
    const [isPending, setIsPending] = useState(true);
    const [error, setError] = useState(null);

    // const handleDelete = (id) => {
    //     const newBlogs = blogs.filter(blog => blog.id !== id);
    //     setBlogs(newBlogs);
    // }

    // let name = 'mario'
    const [name, setName] = useState('mario');
    const [age, setAge] = useState('25');

    const handleClick = () => {
        setName('luigi');
        setAge(30);
    }

    const handleClickAgain = (name, e) => {
        setName('mario');
        setAge(25);
        // console.log('hello ' + name, e.target);
    }

    // useEffect(() => {
    //     // run code that you need to run for render, like fetching data
    //     console.log('use effect ran');
    //     console.log(name);
    // }, [name]);

    useEffect(() => {
        // setTimeout(() => {
            fetch('http://localhost:8000/blogs')
            .then(res => {
                if(!res.ok) {
                    throw Error('could not fetch the data for that resource');
                }
                return res.json();
            })
            .then((data) => {
                setBlogs(data);
                setIsPending(false);
            })
            .catch(err => {
                setIsPending(false);
                setError(err.message);
            });
        // }, 1000);
    }, []);

    return (
        <div className="home">
            { error && <div> {error} </div> }
            { isPending && <div>Loading...</div> }

            {blogs && <BlogList blogs = {blogs} title="All Blogs"/>}
            {blogs && <BlogList blogs = {blogs.filter((blog) => blog.author === 'mario')} title="Mario's Blogs"/>}

            {/* {blogs && <BlogList blogs = {blogs} title="All Blogs" handleDelete={handleDelete}/>}
            {blogs && <BlogList blogs = {blogs.filter((blog) => blog.author === 'mario')} title="Mario's Blogs" handleDelete={handleDelete}/>} */}

            <h2>Homepage</h2>
            <p>{ name } is { age } years old</p>
            <button onClick={handleClick}>Click me</button>
            <button onClick={(e) => {handleClickAgain('mario', e)}}>Click me again</button>
        </div>
    );
}

export default Home;
